DATA SEGMENT     X1 DB 'ASM2015','$'     COUNT EQU $-X1-1DATA ENDSSTACK SEGMENT    DB 100 DUP(0)STACK ENDSCODE SEGMENT    ASSUME CS:CODE,DS:DATA,SS:STACKSTART:    MOV AX,DATA    MOV DS,AX    MOV DX, COUNT    CALL BUBBLESORT    MOV AH, 9    INT 21H    MOV AH,4CH    INT 21H    BUBBLESORT PROC	PUSH CX	PUSH AXNEXT2:LEA SI, X1	MOV CX, DX; COUNT OF INNER LOOPNEXT:MOV AL, [SI]	CMP AL, [SI + 1]	JAE NEXT1; ABOVE/EQUAL, NO ECHG	XCHG AL, [SI + 1]	MOV [SI], ALNEXT1:INC SI	LOOP NEXT ;NEW INNER LOOP	DEC DX	JNZ NEXT2 ;NEW OUTER LOOP	POP AX	POP CX	RETBUBBLESORT ENDPCODE ENDS    END START